
import { BattleCard, BattleScenario, BattleTopic, NpcType } from "../types";
import { NPC_NAMES } from "../constants";

// Helper to create cards
// Updated to accept hideEmoji and use text as primary name
const c = (name: string, desc: string, isCorrect: boolean, type: 'TALK' | 'ACTION' = 'TALK', hideEmoji: boolean = false): BattleCard => ({
  id: Math.random().toString(36).substr(2, 9),
  name, // This is now the full answer text
  description: desc, // This is now empty (was the text, prefix removed)
  isCorrect,
  actionType: type,
  emoji: hideEmoji ? undefined : (isCorrect ? (type === 'ACTION' ? '👍' : '💬') : (type === 'ACTION' ? '👎' : '💢'))
});

interface QuestionTemplate {
    q: string;
    topic: BattleTopic;
    correct: [string, string][]; // 2 correct options
    wrong: [string, string][];   // 8 wrong options
}

const createQ = (q: string, topic: BattleTopic, correct: [string, string][], wrong: [string, string][]): QuestionTemplate => ({
    q, topic, correct, wrong
});

