// Generic Generators for fallback (should not be reached if coverage is 100%)
const generateGenericScenarios = (npcType: NpcType): QuestionTemplate[] => {
    return Array.from({length: 10}).map((_, i) => createQ(
        `[${NPC_NAMES[npcType]}] 这是第${i+1}个考验你的问题，看招！`,
        BattleTopic.LIFE,
        [["正确应对", "完美的回击！"], ["机智回答", "巧妙的化解！"]],
        [["糊弄", "啊对对对。"], ["无厘头", "虽然但是，依托答辩。"], ["发疯", "这福气给你你要不要！"], ["卖惨", "我已经三天没吃饭了。"], ["沉默", "（眼神清澈的愚蠢）"], ["逃跑", "（试图萌混过关）"], ["阴阳", "您说得都对，但我不想听。"], ["摆烂", "毁灭吧，累了。"]]
    ));
};

const GET_SCENARIOS = (npcType: NpcType): QuestionTemplate[] => {
    return FULL_SCENARIOS[npcType] || generateGenericScenarios(npcType);
}

const HOME_NPCS = [
    NpcType.DAD, NpcType.MOM, NpcType.GRANDMA, 
    NpcType.AUNT, NpcType.UNCLE, NpcType.COUSIN, NpcType.KID
];

export const getRandomBattleScenario = (npcType: NpcType, usedIds: string[] = []): BattleScenario => {
  const templates = GET_SCENARIOS(npcType);
  
  // Find available templates based on index
  const availableIndices = templates
    .map((_, idx) => idx)
    .filter(idx => !usedIds.includes(`${npcType}_q_${idx}`));
  
  let selectedIndex = 0;
  if (availableIndices.length > 0) {
      selectedIndex = availableIndices[Math.floor(Math.random() * availableIndices.length)];
  } else {
      // Fallback: Pick random if all exhausted
      selectedIndex = Math.floor(Math.random() * templates.length);
  }

  const template = templates[selectedIndex];
  const stableId = `${npcType}_q_${selectedIndex}`;

  const isHomeNpc = HOME_NPCS.includes(npcType);

  const cards: BattleCard[] = [];
  
  // Add Correct Cards
  template.correct.forEach(([prefix, text]) => {
      // Determine actionType. If text starts with ( or （, it's ACTION.
      const trimmed = text.trim();
      const actionType = (trimmed.startsWith("（") || trimmed.startsWith("(")) ? 'ACTION' : 'TALK';
      
      // Use text as the name (title), empty string as description (to hide summary/prefix)
      cards.push(c(text, "", true, actionType, isHomeNpc));
  });
  // Add Wrong Cards
  template.wrong.forEach(([prefix, text]) => {
      const trimmed = text.trim();
      const actionType = (trimmed.startsWith("（") || trimmed.startsWith("(")) ? 'ACTION' : 'TALK';
      cards.push(c(text, "", false, actionType, isHomeNpc));
  });

  return {
    id: `bs_${Math.random().toString(36).substr(2, 9)}`,
    stableId: stableId,
    npcType,
    topic: template.topic,
    dialogue: template.q,
    cards: cards 
  };
};

export const drawHandFromScenario = (scenario: BattleScenario): BattleCard[] => {
  const correctCards = scenario.cards.filter(c => c.isCorrect);
  const wrongCards = scenario.cards.filter(c => !c.isCorrect);
  
  const hand: BattleCard[] = [];
  
  // 1. MUST have at least 1 correct card
  if (correctCards.length > 0) {
      const idx = Math.floor(Math.random() * correctCards.length);
      hand.push(correctCards[idx]);
  }
  
  // 2. Fill the rest with wrong cards to reach hand size of 3
  const HAND_SIZE = 3;
  const shuffledWrong = [...wrongCards].sort(() => Math.random() - 0.5);
  
  while (hand.length < HAND_SIZE && shuffledWrong.length > 0) {
      hand.push(shuffledWrong.pop()!);
  }
  
  return hand.sort(() => Math.random() - 0.5);
};
